import React, { Component } from "react";
import {
  Label,
  Rating,
  Image,
  Card,
  Modal,
  Header,
  Button,
  Icon,
  Popup
} from "semantic-ui-react";
import defaultBook from '../images/defaultBook.jpg'

export class BookCard extends Component {

  handleClick = () => {
    this.props.onFavoriteBook(this.props.book);
  };

  addDefaultSrc(event){
    event.target.src = {defaultBook}
  }

  render() {

    return (
      <Modal
        trigger={
          <div>
            <Card.Group>
              <Card>
                <Image
                  size="medium"
                  bordered
                  rounded
                  src={this.props.book.volumeInfo.imageLinks}
                  onError={this.addDefaultSrc}
                />
                <Card.Content>
                  <Card.Header>{this.props.book.volumeInfo.title}</Card.Header>
                  <Card.Meta>{this.props.book.volumeInfo.authors}</Card.Meta>
                </Card.Content>
                <Card.Content textAlign="centered" extra>
                  <Rating
                    icon="star"
                    defaultRating={this.props.book.volumeInfo.averageRating}
                    maxRating={5}
                    clearable
                  ></Rating>
                </Card.Content>
                <Card.Content extra>
                  <Label color="blue">
                    Reviews
                    <Label.Detail>
                      {this.props.book.volumeInfo.ratingsCount}
                    </Label.Detail>
                  </Label>
                </Card.Content>
              </Card>
            </Card.Group>
          </div>
        }
      >
        <Header content="Description" />
        <Modal.Content>
          <p>{this.props.book.volumeInfo.description}</p>
        </Modal.Content>
        <Modal.Actions>
          <Button color="red" onClick={() => this.handleClick()}>
            <Popup
              trigger={<Icon size="large" name="heart" />}
              content="You favorited this book."
              inverted
            />
          </Button>
        </Modal.Actions>
      </Modal>
    );
  }
}

export default BookCard;
