import React, { Component } from 'react'
import BookCard from './BookCard'
import {Grid, Container} from 'semantic-ui-react'

var favoritesUrl = 'http://localhost:3001/favorites'

export class BookCollection extends Component {


  favoriteBook = (book) => {
    const {user_id} = this.props
    var book_string = JSON.stringify(book)

    fetch(favoritesUrl, {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        "user_id": user_id,
        "book_string": book_string
      })
    })
  }

  getFavoriteBooks = () => {
    var favoritesUrl = `http://localhost:3001/favorites`

    fetch(favoritesUrl)
    .then(response => {
    return response.json()
    })
    .then(data => {
        this.setState({
            data: this.props.book
        })
    })
}   

    render(){
        return (
        <Container>
              <Grid divided>
                    {this.props.books.map((book,index) => (
                    <div key={index}>
                    <BookCard onFavoriteBook={this.favoriteBook} book={book} getFavoriteBooks={this.getFavoriteBooks} />
                    </div>))}
            </Grid>
        </Container>
        );
    }

  };

export default BookCollection
