import React, { Component } from 'react'
import BookCollection from './BookCollection'

export class BookContainer extends Component {

    state = {
        searchValue: '',
        books: []
    }

    getBooks = searchInput => {
        var searchUrl = `https://www.googleapis.com/books/v1/volumes?q=${searchInput}&maxResults=36`;

        fetch(searchUrl)
        .then(response => {
        return response.json();
        })
        .then(data => {
            this.setState({
                books: data.items
            })
        });
        };

    handleOnChange = event => {
        this.setState({
            searchValue: event.target.value
        })
    }

    handleSearch = () => {
        this.getBooks(this.state.searchValue)
    }

    render() {
        return (
            <div class="ui grid">
                <div class="sixteen wide column">
                    <div class="ui hidden divider">
                        <div class="ui search"> 
                            <div class="ui icon input">
                                <input 
                                class="field"
                                type="text"
                                placeholder="Search books..."
                                onChange={event => this.handleOnChange(event)}
                                value={this.state.searchValue}
                                />
                                <i class="circular search link icon" onClick={this.handleSearch}></i>
                            </div>
                        </div>
                    </div>
                </div>
            </div>

        )
    }
}

export default BookContainer
