import React, { Component } from 'react'
import { Card, Image, Icon } from 'semantic-ui-react'

export class BookCard extends Component {
    render() {
        return (
            <Card>
            <Image src={this.props.book.volumeInfo.imageLinks.thumbnail} wrapped ui={false} />
            <Card.Content>
              <Card.Header>{this.props.book.volumeInfo.title}</Card.Header>
              <Card.Meta>{this.props.book.volumeInfo.authors}</Card.Meta>
              {/* <Card.Description>
                Daniel is a comedian living in Nashville.
              </Card.Description> */}
            </Card.Content>
            <Card.Content extra>
                <Icon name='star' />
                10 Friends
            </Card.Content>
          </Card>
        )
    }
}

export default BookCard
