import React, { Component } from "react";
import {
  Label,
  Rating,
  Image,
  Card,
  Modal,
  Header,
  Button,
  Icon,
  Popup
} from "semantic-ui-react";

export class BookCard extends Component {
  handleClick = () => {
    this.props.onFavoriteBook(this.props.book);
  };

  render() {
    const { book } = this.props.book.volumeInfo;

    return (
      <Modal
        trigger={
          <div>
            <Card.Group>
              <Card>
                <Image
                  size="medium"
                  bordered
                  rounded
                  src={book.imageLinks.thumbnail}
                />
                <Card.Content>
                  <Card.Header>{book.title}</Card.Header>
                  <Card.Meta>{book.authors}</Card.Meta>
                </Card.Content>
                <Card.Content textAlign="centered" extra>
                  <Rating
                    icon="star"
                    defaultRating={book.averageRating}
                    maxRating={5}
                    clearable
                  ></Rating>
                </Card.Content>
                <Card.Content extra>
                  <Label color="blue">
                    Reviews
                    <Label.Detail>
                      {book.ratingsCount}
                    </Label.Detail>
                  </Label>
                </Card.Content>
              </Card>
            </Card.Group>
          </div>
        }
      >
        <Header content="Description" />
        <Modal.Content>
          <p>{book.description}</p>
        </Modal.Content>
        <Modal.Actions>
          <Button color="red" onClick={() => this.handleClick()}>
            <Popup
              trigger={<Icon size="large" name="heart" />}
              content="You favorited this book."
              inverted
            />
          </Button>
        </Modal.Actions>
      </Modal>
    );
  }
}

export default BookCard;
