import React, { Component } from 'react'
import {List, Image, Card} from 'semantic-ui-react'

export class Favorites extends Component {

    // will need user_id 

    state = {
        favoriteBooks: []
    }

    showFavoriteBooks = () => {
        var favoriteBooks = 'http://localhost:3001/favorites';

        fetch(favoriteBooks)
        .then(response => {
        return response.json();
        })
        .then(data => {
            this.setState({
                favoriteBooks: data
            })
        });
        };



    render() {
        return (
            <div>
                <Card.Group> 
                    <Card onClick={() => this.showFavoriteBooks()}>
                    {this.state.favoriteBooks.map((book,index) => (
                    <div key={index}>
                    <p>{book.title}</p>                 
                    </div>))}
                        <Image 
                        size='medium'
                        bordered
                        rounded
                        src={book.volumeInfo.imageLinks.thumbnail}
                        />
                        <Card.Content>
                            <Card.Header>{book.volumeInfo.title}</Card.Header>
                            <Card.Meta>{book.volumeInfo.authors[0]}</Card.Meta>
                        </Card.Content>
                    </Card>
                </Card.Group>





            {/* <List>
               <List.Item>
                    <Image onClick={() => this.showFavoriteBooks()} src={'https://react.semantic-ui.com/images/avatar/small/rachel.png'} />
                    {this.state.favoriteBooks.map((book,index) => (
                    <div key={index}>
                    <p>{book.title}</p>                 
                    </div>))}
                </List.Item> 
            </List> */}
            </div>
        )
    }
}

export default Favorites
