import React, { Component } from 'react'
import FavoritesCard from './FavoritesCard'
import {Container,Grid} from 'semantic-ui-react'

export class Favorites extends Component {

    state = {
        favoriteBooks: []
    }

    showFavoriteBooks = () => {
        var favoriteBooks = 'http://localhost:3001/favorites';

        fetch(favoriteBooks)
        .then(response => {
        return response.json();
        })
        .then(data => {
            this.setState({
                favoriteBooks: data
            })
        });
        };

    componentDidMount() {
        this.showFavoriteBooks()
    }

    removeBook = (book) => {
        this.setState({
            favoriteBooks: this.deleteBook(book)
        })
    }

    deleteBook =(book) => {
        let newArray = this.state.favoriteBooks.slice()
        let index = 0
        newArray.forEach(b => {
            if(b===book)
            index = newArray.indexOf(b)
        })
        newArray.splice(index, 1)
        return newArray
    }

    render() {
        return (
            <div>
                <h2>Your Favorites</h2>
            <div
                style={{
                position: 'absolute', 
                left: '35%', 
                top: '40%',
                transform: 'translate(-50%, -50%)' }}>
            <Container>
            <Grid divided>
                {this.state.favoriteBooks.map((book,index) => (
                <div key={index}>
                <FavoritesCard onClick={this.deleteBook} book={book}/>
                </div>
                ))}   
            </Grid>
            </Container>
        </div>
        </div>
        )
    }
}

export default Favorites
