import React, { Component } from 'react'
import {List, Image, Card} from 'semantic-ui-react'

export class Favorites extends Component {

    // will need user_id 

    state = {
        favoriteBooks: []
    }

    showFavoriteBooks = () => {
        var favoriteBooks = 'http://localhost:3001/favorites';

        fetch(favoriteBooks)
        .then(response => {
        return response.json();
        })
        .then(data => {
            this.setState({
                favoriteBooks: data
            })
        });
        };



    render() {
        return (
            <div>
                 {this.showFavoriteBooks()}
            </div>
        )
    }
}

export default Favorites
