import React, { Component } from 'react'
import { Rating } from 'semantic-ui-react'
// import BookDetails from './BookDetails'


export class BookCard extends Component {


    handleClick = () => {
        console.log('you are in click')
    }

    render() {
        return (
            <div class="ui four column grid">
            <div class="column">
            <div class="ui fluid cards">
            <div class="card">
              <div class="image">
                <img src={this.props.book.volumeInfo.imageLinks.thumbnail} alt="book"/>
              </div>
              <div class="content">
                <div class="title">{this.props.book.volumeInfo.title}</div>
                <div class="authors">
                  <a>{this.props.book.volumeInfo.authors}</a>
                </div>
                <div class="description">
                  {this.props.book.title}
                </div>
              </div>
              <div class="extra content">
                <span class="right floated">
                {this.props.book.volumeInfo.ratingsCount} ratings
                </span>
                <span class="left floated">
                <Rating icon='star' defaultRating={this.props.book.volumeInfo.averageRating} maxRating={5}/>
                </span>
              </div>
            </div>
          </div>
          </div>
          </div>
        )
    }
}

export default BookCard


