import React, { Component } from 'react'
import {Label, Rating, Image, Card} from 'semantic-ui-react'

export class BookCard extends Component {


    handleClick = () => {
        console.log('you are in click')
    }

    render() {
        return (
                <Card.Group> 
                    <Card>

                    <Segment padded>
                        <Label attached='top right'>Details </Label>
                        <Image src={this.props.book.volumeInfo.imageLinks.thumbnail} />
                        </Segment>
                        <Image 
                        // fluid
                        // label={{ as: 'a', corner: 'left', icon: 'heart', color: 'red'}}
                        src={this.props.book.volumeInfo.imageLinks.thumbnail}
                        />
                        <Card.Content>
                            <Card.Header>{this.props.book.volumeInfo.title}</Card.Header>
                            <Card.Meta>{this.props.book.volumeInfo.authors}</Card.Meta>
                        </Card.Content>
                        <Card.Content extra>
                            <Rating icon="star" defaultRating={this.props.book.volumeInfo.averageRating} maxRating={5} clearable></Rating>
                            <Label>
                                Reviews
                                <Label.Detail>{this.props.book.volumeInfo.ratingsCount}</Label.Detail>
                            </Label>
                        </Card.Content>
                    </Card>
                </Card.Group>
        )
    }
}

export default BookCard
