import React, { Component } from 'react'
import { Rating } from 'semantic-ui-react'
// import BookDetails from './BookDetails'


export class BookCard extends Component {


    handleClick = () => {
        console.log('you are in click')
    }

    render() {
        return (
            <div class="ui cards">
            <div class="img">
                <img src={this.props.book.volumeInfo.imageLinks.thumbnail} alt="book"/>
            </div>
            <div class="content">
                <a class="title">{this.props.book.volumeInfo.title}</a>
                <div class="meta">
                <span class="authors">{this.props.book.volumeInfo.authors}</span>
                </div>
                <div class="description">
                Kristy is an art director living in New York.
                </div>
            </div>
            <div class="extra content">
                <Rating
                icon='star' defaultRating={this.props.book.volumeInfo.averageRating} maxRating={5}/>
                {this.props.book.volumeInfo.ratingsCount} ratings/>
            </div>
            </div>
