import React, { Component } from 'react'
import { Card, Image, Rating } from 'semantic-ui-react'

export class BookCard extends Component {
    render() {
        return (
            <Card>
            <Image src={this.props.book.volumeInfo.imageLinks.thumbnail} wrapped ui={false} />
            <Card.Content>
              <Card.Header>{this.props.book.volumeInfo.title}</Card.Header>
              <Card.Meta>{this.props.book.volumeInfo.authors}</Card.Meta>
            </Card.Content>
            <Card.Content extra>
                <Rating icon='star' defaultRating={this.props.book.volumeInfo.averageRating} maxRating={5}/>
                {this.props.book.volumeInfo.ratingsCount} ratings
            </Card.Content>
          </Card>
        )
    }
}

export default BookCard
