        fetch(favoritesUrl)
        .then(response => {
        console.log(response);
        })
    }

    handleClick = () => {
        console.log(this.props.book)
    }



    render() {
        return (
            <div>
            <List>
               <List.Item>
                    <Image onClick={() => this.handleClick()} src='https://react.semantic-ui.com/images/avatar/small/rachel.png' />
                    <List.Content>
                        <List.Header as='a'>Rachel</List.Header>
                            <List.Description>
                            <b>Arrested Development</b>
                            </List.Description>
                    </List.Content>
                </List.Item> 
            </List>
            </div>
        )
    }
}

export default Favorites
