    favoriteBook = (book) => {

    const {user_id} = this.props
    var book_string = JSON.stringify(book)

    console.log(book.volumeInfo.title)
    fetch(favoritesUrl, {
      method: 'POST',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        "user_id": user_id,
