import React, { Component } from 'react'
import {Container, Grid, Image, Card} from 'semantic-ui-react'

export class Favorites extends Component {

    state = {
        favoriteBooks: []
    }

    showFavoriteBooks = (props) => {
        var favoriteBooks = 'http://localhost:3001/favorites';

        fetch(favoriteBooks)
        .then(response => {
        return response.json();
        })
        .then(data => {
            this.setState({
                favoriteBooks: this.props.data
            })
        });
        };



    render() {
        return (
        <div onLoad={() => this.showFavoriteBooks()}>
            {this.state.favoriteBooks.map((book,index) => (
                <div key={index}>
                    <Image 
                    size='medium'
                    bordered
                    rounded
                    src={'https://react.semantic-ui.com/images/avatar/small/rachel.png'}
                    />

                    {book.title}
                    {book.authors}

                </div>))}
        </div>           
        )
    }
}

export default Favorites
