import React, { Component } from 'react'
import { Rating } from 'semantic-ui-css'
// import BookDetails from './BookDetails'


export class BookCard extends Component {


    handleClick = () => {
        console.log('you are in click')
    }
    render() {
        return (
            <div className="ui link cards">
            <div className="card">
              <div className="image">
                <img src={this.props.book.volumeInfo.imageLinks.thumbnail} alt="book"/>
              </div>
              <div className="content">
                <div className="title">{this.props.book.volumeInfo.title}</div>
                <div className="authors">
                  {this.props.book.volumeInfo.authors}
                </div>
                <div className="description">
                  {this.props.book.title}
                </div>
              </div>
              <div className="extra content">
                <span className="right floated">
                {this.props.book.volumeInfo.ratingsCount} ratings
                </span>
                <span className="left floated">
                {/* <Rating icon='star' defaultRating={this.props.book.volumeInfo.averageRating} maxRating={5}/> */}
                </span>
              </div>
            </div>
          </div>
        )
    }
}

export default BookCard
