import React, { Component } from 'react'
import BookCard from './BookCard'
import {Grid, Container} from 'semantic-ui-react'

var favoritesUrl = 'http://localhost:3001/api/v1favorites'

export class BookCollection extends Component {


  favoriteBook = (book) => {
    const {user_id} = this.props
    var book_string = JSON.stringify(book)
    fetch(favoritesUrl, {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: {
        "user_id": user_id,
        "book_string": book_string
      }
    })

    // make fetch to backend to save book and user id
  }

    render(){
        return (
        <Container>
              <Grid divided>
                    {this.props.books.map((book,index) => (
                    <div key={index}>
                    <BookCard onFavoriteBook={this.favoriteBook} book={book}/>
                    </div>))}
            </Grid>
        </Container>
        );
    }

  };

export default BookCollection
