import React, { Component } from 'react'
import {List, Image} from 'semantic-ui-react'

export class Favorites extends Component {

    // will need user_id 

    state = {
        favoriteBooks: []
    }

    showFavoriteBooks = () => {
        var favoriteBooks = 'http://localhost:3001/favorites';

        fetch(favoriteBooks)
        .then(response => {
        return response.json();
        })
        .then(data => {
            this.setState({
                favoriteBooks: data
            })
        });
        };



    render() {
        return (
            <div>
                <Card.Group> 
                    <Card>
                        <Image 
                        size='medium'
                        bordered
                        rounded
                        src={this.props.book.volumeInfo.imageLinks.thumbnail}
                        />
                        <Card.Content>
                            <Card.Header>{this.props.book.volumeInfo.title}</Card.Header>
                            <Card.Meta>{this.props.book.volumeInfo.authors}</Card.Meta>
                        </Card.Content>
                        <Card.Content extra>
                            <Rating icon="star" defaultRating={this.props.book.volumeInfo.averageRating} maxRating={5} clearable></Rating>
                            <Label>
                                Reviews
                                <Label.Detail>{this.props.book.volumeInfo.ratingsCount}</Label.Detail>
                            </Label>
                        </Card.Content>
                    </Card>
                </Card.Group>





            <List>
               <List.Item>
                    <Image onClick={() => this.showFavoriteBooks()} src={'https://react.semantic-ui.com/images/avatar/small/rachel.png'} />
                    {this.state.favoriteBooks.map((book,index) => (
                    <div key={index}>
                    <p>{book.title}</p>                 
                    </div>))}
                </List.Item> 
            </List>
            </div>
        )
    }
}

export default Favorites
